children.value:
display: grid;
text-align: center;
grid-template-columns: repeat(

, 2fr) 1fr;

  > div {
    border: solid 1px black;
    border-left: none;
    height: 1rem;
    &:nth-child(2) {
      border-right: solid 1px black;
    }
    &:first-child,
    &:last-child {
      border: none;
    }
  }
